import React, { Component, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import '../css/SignUp.css';
import { USER_URL } from "../constants";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SignUp = () => {

  var alert = false;
  const nav = useNavigate();
    const [user, setUser] = useState({
      email: "",
      password: "",
      firstname: "",
      lastname: "",
      university: "",
      dept: "",
      major: "",
      isAdmin: 0
    });

  const dept = ['Arts and Sciences', 'Engineering', 'Fine arts', 'Business', 'Education', 'Communication', 'Health sciences', 'Social Work', 'Theatre'];

  const fetchData = async() => {

    await axios.get(USER_URL)
      .then(res => {
        console.log(res.data)
      })

    await axios.post(USER_URL, user)
    .then(res => {
        if(res.data.userId != '')
          nav('/home')
        else {
          alert = true;
          console.log('no user id');
          showAlert();
        }
    })
  }

  const showAlert = () => {
    console.log('alert function');
    if(alert) {
      console.log('alert function true');
      return (
        <div className="alert">
          <p>This is a danger alertâ€”check it out!</p>
        </div>
      );
    }
  }

  const handleChange = (event) => {
    let target = event.target;
    let value = target.value;
    let name = target.name;

    setUser({
      ...user,
      [name]: value
    });
  }

  const handleSubmit = (e) => {

    e.preventDefault();
    fetchData();
    console.log('handleSubmit', user);
  }
    return (
      <div className="appForm">
      <h2 className="signupHeading">Signup</h2>
      <div className="formCenter">
        <form onSubmit={handleSubmit} className="formFields">
          <div className="formField">
            <label className="formFieldLabel" htmlFor="firstname">
              First Name
            </label>
            <input
              type="text"
              className="formFieldInput"
              placeholder="Enter your first name"
              name="firstname"
              value={user.firstname}
              onChange={handleChange}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="lastname">
              Last Name
            </label>
            <input
              type="text"
              className="formFieldInput"
              placeholder="Enter your last name"
              name="lastname"
              value={user.lastname}
              onChange={handleChange}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="password">
              Password
            </label>
            <input
              type="password"
              className="formFieldInput"
              placeholder="Enter your password"
              name="password"
              value={user.password}
              onChange={handleChange}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="email">
              E-Mail Address
            </label>
            <input
              type="email"
              className="formFieldInput"
              placeholder="Enter your email"
              name="email"
              value={user.email}
              onChange={handleChange}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="university">
              University/College
            </label>
            <input
              type="text"
              className="formFieldInput"
              placeholder="Enter your university/college name"
              name="university"
              value={user.university}
              onChange={handleChange}
            />
          </div>
          <div className="formField">
            <label className= "formFieldLabel" htmlFor="department">
              Department
            </label>
            <select name="dept" onChange={handleChange}>
              {dept.map((dt) => 
              <option value={dt}>{dt}</option>
              )}
            </select>
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="major">
              Major
            </label>
            <input
              type="text"
              className="formFieldInput"
              placeholder="Enter your major"
              name="major"
              value={user.major}
              onChange={handleChange}
            />
          </div>
          <div className="formField">
            <button className="formFieldButton">Sign Up</button>{" "}
          </div>
          <Link to="/sign-in" className="formFieldLink">
            I'm already member
          </Link>
        </form>
        <div>
          {showAlert()}
          </div>
      </div>
      </div>
    );
  }
export default SignUp;
