import React, { Component, useState } from "react";
import { Link } from "react-router-dom";
import '../css/SignUp.css';
import { USER_URL } from "../constants";
import axios from 'axios';

const SignUp = () => {

    const state = {
      email: "",
      password: "",
      username: "",
      university: "",
      dept: "",
      major: "",
      isAdmin: 0
    };

    //this.handleChange = this.handleChange.bind(this);
    //this.handleSubmit = this.handleSubmit.bind(this);
    //this.componentDidMount = this.componentDidMount.bind(this);

  const dept = ['Arts and Sciences', 'Engineering', 'Fine arts', 'Business', 'Education', 'Communication', 'Health sciences', 'Social Work', 'Theatre'];

  const fetchData = () => {

    axios.get(USER_URL)
      .then(res => {
        console.log(res.data)
      })

    axios.post(USER_URL, state)
    .then(res => {
        this.setState({
            data: state
        })
        console.log('componentDidMount', state);
    })
  }

  const handleChange = (event) => {
    let target = event.target;
    //let value = target.type === "checkbox" ? target.checked : target.value;
    let name = target.name;

    this.setState({
       [name]: value
    });
  }

  const handleSubmit = () => {

    console.log(state);
    fetchData();

    console.log("The form was submitted with the following data:");
    console.log('handleSubmit', state);
  }
    return (
      <div className="SignUp">
        <div className="appForm">
      <div className="formCenter">
        <form onSubmit="handleSubmit()" className="formFields">
          <div className="formField">
            <label className="formFieldLabel" htmlFor="username">
              Full Name
            </label>
            <input
              type="text"
              id="username"
              className="formFieldInput"
              placeholder="Enter your full name"
              name="username"
              value={state.username}
              onChange="handleChange()"
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="password">
              Password
            </label>
            <input
              type="password"
              id="password"
              className="formFieldInput"
              placeholder="Enter your password"
              name="password"
              value={state.password}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="email">
              E-Mail Address
            </label>
            <input
              type="email"
              className="formFieldInput"
              placeholder="Enter your email"
              name="email"
              value={state.email}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="email">
              University/College
            </label>
            <input
              type="text"
              className="formFieldInput"
              placeholder="Enter your university/college name"
              name="university"
              value={state.university}
            />
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="dept">
              Department
            </label>
            <select name="dept" value={state.dept}>
              {dept.map((dt) => 
              <option value="{dt}">{dt}</option>
              )}
            </select>
          </div>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="email">
              Major
            </label>
            <input
              type="text"
              className="formFieldInput"
              placeholder="Enter your university/college name"
              name="major"
              value={state.major}
            />
          </div>
          <div className="formField">
            <button className="formFieldButton">Sign Up</button>{" "}
          </div>
          <Link to="/sign-in" className="formFieldLink">
            I'm already member
          </Link>
        </form>
      </div>
      </div>
      </div>
    );
  }
export default SignUp;
