import React from 'react';
import { Component, useState } from 'react';
import {Button} from 'react-bootstrap'
import Card from "react-bootstrap/Card";
import '../css/Home.css';
import Header from './Header.js';

const [posts, setPosts] = useState([]);

export default class Home extends Component {
    constructor() {
      super();
    }
    columnsPerRow = 4;
    getPostsData = async () => {
        try {
          await apiData
            .getPosts(numberOfPosts)
            .then((data) => {    
              setPosts(data.json);
            });
        } catch (err) {
          console.log(err);
        }
      };

    render() {
        const getColumnsForRow =()=>{
            let items = posts.map((post, index) => {
              return ( 
                <Col>
                <Card key={post.id}>
                <Card.Body>
                  <Card.Title>{parse(post.title.rendered)}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">{post.categories[0]}</Card.Subtitle>
                  <Card.Text>
                    {parse(post.excerpt.rendered)}
                  </Card.Text>
                  <Card.Link href={post.link}>View post</Card.Link>                 
                </Card.Body>
              </Card>
            </Col>
              );
       
          });
          return items;
        };
        useEffect(() => {
            getPostsData();        
       })
}
}
