import React, { useState } from 'react';
import { ListGroup } from 'react-bootstrap';
import Header from './Header';
import Cookies from 'js-cookie';
import axios from 'axios';
import { FETCH_REQUESTS_URL, FETCH_COMM_ADMIN_URL } from '../constants';
import '../css/FetchUsers.css';

const FetchUsers = () => {

  const [data, setData] = useState([]);
  const [show, setShow] = useState(false);
  const [channelName, setchName] = useState("");
  const [channelId, setchId] = useState();
  const [requests, setReq] = useState([]);
  
  const fetchData = () => {
    const req = {"userId": Cookies.get("userId")};
      axios.post(FETCH_COMM_ADMIN_URL, req)
      .then(res => {
        setData(res.data);
        console.log('requests123456:', res.data);
      });
  }

  React.useEffect(() => {
    fetchData();
  }, [])

  const selectComm = (chId, name) => {
    console.log('select');
    setShow(true);
    setchName(name);
    setchId(chId);
    const req = {'chId': chId};
    axios.post(FETCH_REQUESTS_URL, req)
      .then(res => {
        setReq(res.data);
        console.log(res.data);
        for(let i=0; i<res.data.length; i++) {
          requests.push(res.data[i].requests);
        }
        console.log('requests123456:',requests);
      });
  }

  const showUsers = () => {
    console.log('requests', requests[channelId]);
    if(show) {
      return (
        <div className="mesgs">
          <h3 className="channelHead">{channelName}</h3>
          {requests.map((req) =>
            <div id="fb">
              <img src="https://s13.postimg.org/xgla0jo4n/image.jpg" alt="Image of woman" />
              <p><b>{req.firstname} {req.lastname}</b> <br/></p>
              <p><b>Department: </b> <span>{req.dept}</span></p>
              <div id="button-block">
                  <div id="confirm">Approve</div>
                  <div id="delete">Reject</div>
              </div>
            </div>
          )}
        </div>
      );
    }
    else {
      return (
        <div class="mesgs">
          <h1>Click on a community to see the user requests.</h1>
        </div>
      );
    }
  }

    return (
        <div>
          <Header/>
          <div className="container">
            <div className="appAside">
              <h2 className="chnlHead">Communities</h2> 
              <ListGroup>
                {data.map((dt) => 
                  <ListGroup.Item action onClick={() => selectComm(dt.chId, dt.name)}>
                    <div className="ms-2 me-auto">
                      <div className="fw-bold">{dt.name}</div>
                      {dt.info}
                    </div>
                  </ListGroup.Item>
                )}
              </ListGroup>
            </div>
            <div>
                {showUsers()}
            </div>
          </div>
        </div>
    );
}

export default FetchUsers;
