import React, { useState } from 'react';
import { ListGroup } from 'react-bootstrap';
import Header from './Header';
import Cookies from 'js-cookie';
import axios from 'axios';
import { FETCH_USER_URL } from '../constants';
import '../css/FetchUsers.css';
import UserList from './UserList';

const FetchUsers = () => {

  const [data, setData] = useState([])
  var requests = [];
  
  const fetchData = () => {
    const req = {"userId": Cookies.get("userId")};
      axios.post(FETCH_USER_URL, req)
      .then(res => {
        setData(res.data);
        console.log(res.data);
        for(let i=0; i<res.data.length; i++) {
          requests.push(res.data[i].requests);
        }
      });
  }

  React.useEffect(() => {
    fetchData();
  }, [])

  const selectComm = (chId) => {
    return <UserList chId={chId} requests={requests[chId]} />;
  }

    return (
        <div>
        <Header/>
        <div className="container">
          <div className="appAside">
            <h2 className="chnlHead">Communities</h2> 
            <ListGroup>
              {data.map((dt) => 
                <ListGroup.Item action onClick={() => selectComm(dt.chId)}>
                  <div className="ms-2 me-auto">
                    <div className="fw-bold">{dt.name}</div>
                    {dt.info}
                  </div>
                </ListGroup.Item>
              )}
            </ListGroup>
          </div>
          <div>
          <div className="mesgs">
        <h3 className="channelHead">{}</h3>
        {requests.map((req) =>
        <div id="fb">
          <img src="https://s13.postimg.org/xgla0jo4n/image.jpg" height="100" width="100" alt="Image of woman" />
          <p id="info"><b>{req.firstname} {req.lastname}</b> <br/></p>
          <div id="button-block">
            <div id="confirm">Confirm</div>
            <div id="delete">Delete Request</div>
          </div>
        </div>
        )}
        </div>
          </div>
        </div>
      </div>
    );
}

export default FetchUsers;
