import axios from 'axios';
import Cookies from 'js-cookie';
import { useState } from 'react';
import { PROVINCE_URL } from '../constants';
import '../css/Province.css';

const Province = () => {

    const [prov, setProv] = useState("");

    handleChange = (event) => {
        let target = event.target;
        let value = target.value;
        setProv(value);
    }

    const saveProvince = () => {
        Cookies.set("province", prov);
        const req = {'province': prov, 'userId': Cookies.get('userId')};
        console.log('req', req);
        axios.post(PROVINCE_URL, req)
        .then(res => {
            console.log('saved');
        });
    }

    return (
        <div className="provDiv">
            <h1 className="provHead" onChange={handleChange}>Select province</h1>
            <select name="dept" className="provDropdown">
              {/* {dept.map((dt) =>  */}
              <option value="hello">Saskatchewan</option>
              {/* )} */}
            </select>
            <button className="next" onClick={() => saveProvince()}>Next</button>
        </div>
    );
}

export default Province;
